import { useState } from "react";
import { FaTimes, FaClock, FaEdit, FaCheck } from "react-icons/fa";
import { dayName } from "../utls/date";

// componenets
import Task from "./Task";
import TaskEdit from "./TaskEdit";

const TaskSwitch = ({ key, task, deleteTask, toggleReminder }) => {
  const [editTaskToggle, setEditTaskToggle] = useState(false);
  const [editTask, setEditTask] = useState(task.text);
  return (
    <div
      className={`task ${task.reminder ? "reminder" : ""}`}
      onDoubleClick={() => toggleReminder(task.id)}
    >
      <div>
        {editTaskToggle ? (
          <TaskEdit />
        ) : (
          <Task
            key={task.id}
            task={task}
            deleteTask={deleteTask}
            toggleReminder={toggleReminder}
          />
        )}
      </div>
      {editTaskToggle ? (
        <span margin="10">
          <FaCheck
            style={{ fontSize: "10" }}
            onClick={() => setEditTaskToggle(!editTaskToggle)}
          />
        </span>
      ) : (
        <div className="" style={{ width: "100" }}>
          <FaEdit
            style={{
              cursor: "pointer",
              fontSize: "12px",
              marginRight: "10px",
              paddingBottom: "2px",
            }}
            onClick={() => setEditTaskToggle(!editTaskToggle)}
          />
          <FaTimes
            style={{ color: "red", cursor: "pointer" }}
            onClick={() => deleteTask(task.id)}
          />
        </div>
      )}
    </div>
  );
};
export default TaskSwitch;
