import { useState } from 'react'
import { FaTimes, FaClock, FaEdit } from "react-icons/fa";
import {dayName} from "../utls/date"

const Task = ({ task, deleteTask, toggleReminder }) => {
  const [editTaskToggle, setEditTaskToggle] = useState(t);

  return (
    <div
      className={`task ${task.reminder ? "reminder" : ""}`}
      onDoubleClick={() => toggleReminder(task.id)}
    >
      <div>
        {editTaskToggle ? 
        <form>
          <input className="edit" type="text" />
        </form>: <h3>{task.text}</h3>}
        <p>
          <FaClock color="gray" fontSize="10" />
          &nbsp;{dayName(task.day)} {task.day} {task.time && "@"} {task.time}
        </p>
      </div>
      <div className="">
        <FaEdit
          style={{
            cursor: "pointer",
            fontSize: "12px",
            marginRight: "10px",
            paddingBottom: "2px",
          }}
          onClick={() => deleteTask(task.id)}
        />
        <FaTimes
          style={{ color: "red", cursor: "pointer" }}
          onClick={() => deleteTask(task.id)}
        />
      </div>
    </div>
  );
};
export default Task;
