import { useState } from "react";
import { FaClock } from "react-icons/fa";
import { dayName } from "../utls/date";

const TaskEdit = ({
  task,
  deleteTask,
  toggleReminder,
  editedTask,
  setEditedTask,
}) => {
  return (
    <div>
      <div>
        <div>
          <form>
            <textarea
              className="edit"
              type="text-area"
              value={editedTask.text}
              rows={
                editedTask.length < 37 ? 1 : Math.ceil(editedTask.length / 37)
              }
              cols={editedTask.length < 40 ? editedTask.length : 45}
              onChange={(e) =>
                setEditedTask({ ...editedTask, text: e.target.value })
              }
            />
          </form>
          <p color="gray">
            <FaClock color="gray" fontSize="10" />
            &nbsp;{dayName(task.day)} {task.day} {task.time && "@"} {task.time}
          </p>
        </div>
      </div>
    </div>
  );
};
export default TaskEdit;
