import Task from "./Task";
const tasks = [
  {
    id: 1,
    text: "Doctors Appointment",
    day: "Feb 5th at 2:30pm",
    reminder: true,
  },
  {
    id: 2,
    text: "Meeting at School",
    day: "Feb 6th at 1:30pm",
    reminder: true,
  },
];
const Tasks = () => {
  return (
    <div className="tasks">
      {tasks.map((task, i) => {
        return (
          <Task
            id={tasks[i].id}
            text={tasks[i].text}
            day={tasks[i].day}
            reminder={tasks[i].reminder}
          />
        );
      })}
    </div>
  );
};

export default Tasks;
