export const dayName = (date) => {
  const days = [
    "Sunday",
    "Monday",
    "Tuesday",
    "Wednesday",
    "Thursday",
    "Friday",
    "Saturday",
  ];

  return date ? days[new Date().getDay()] : days[new Date().getDay()];
};
