import { useState } from "react";
import { FaTimes, FaClock, FaEdit, FaCheck } from "react-icons/fa";
import { dayName } from "../utls/date";
import TaskEdit from "../componenets/TaskEdit";

const Task = ({ task, deleteTask, toggleReminder }) => {
  const [editTaskToggle, setEditTaskToggle] = useState(false);

  return (
    <div

    >

        <div>
          <div>
            {editTaskToggle ? (
              <form>
                <textarea
                  className="edit"
                  type="text-area"
                  value={editTask}
                  rows={
                    editTask.length < 32 ? 1 : Math.ceil(editTask.length / 32)
                  }
                  cols={editTask.length < 40 ? editTask.length : 40}
                  onChange={(e) => setEditTask(e.target.value)}
                />
              </form>
            ) : (
              <h3>
                {task.text}
              </h3>
            )}
            <p>
              <FaClock color="gray" fontSize="10" />
              &nbsp;{dayName(task.day)} {task.day} {task.time && "@"}{" "}
              {task.time}
            </p>
          </div>

        </div>
      )}
    </div>
  );
};
export default Task;
