class DayName{
   constructor(day) {
      this.day = day;
   }

   Name = (day) => {
       const days = [
         "Sunday",
         "Monday",
         "Tuesday",
         "Wednesday",
         "Thursday",
         "Friday",
         "Saturday",
       ];
      if (!day) {
      return days[new Date().getDay()]
   }
   }
}
export default DayName;
