import Task from './Task' 
const tasks = [
  {
    id: 1,
    text: "Doctors Appointment",
    day: "Feb 5th at 2:30pm",
    reminder: true,
  },
  {
    id: 2,
    text: "Meeting at School",
    day: "Feb 6th at 1:30pm",
    reminder: true,
  }
];
const Tasks = () => {
   return (
     <div className="tasks">
         {tasks.map((task, i ) => {
            `<Task
              id={tasks[0].id}
              text={tasks[0].text}
              day={tasks[0].day}
              reminder={tasks[0].reminder}
            />;`
         })}

     </div>
   );
}

export default Tasks
