import Task from "./Task";

const Tasks = ({ tasks }) => {
  return (
    <>
      {tasks.map((task, i) => (

          <Task
            id={task.id}
            text={task.text}
            day={task.day}
            reminder={task.reminder}
          />

      ))}
    </>
  );
};

export default Tasks;
