import { useState } from "react";
import { FaClock } from "react-icons/fa";
import { dayName } from "../utls/date";

const TaskEdit = ({
  task,
  deleteTask,
  toggleReminder,
  editedTask,
  setEditedTask,
  setMnueItem,
}) => {
  console.log(editedTask);
  return (
    <div style={{ display: "blcok" }} onClick={() => setMnueItem(1)}>
      <div>
        <div>
          <form>
            <textarea
              className="edit"
              autoFocus
              type="text-area"
              value={editedTask.text}
              rows={
                editedTask.text.length < 29
                  ? 1
                  : Math.ceil(editedTask.text.length / 23)
              }
              cols={editedTask.text.length < 30 ? editedTask.text.length : 36}
              onChange={(e) =>
                setEditedTask({ ...editedTask, text: e.target.value })
              }

            />
          </form>
          <p color="gray">
            <FaClock color="gray" fontSize="10" />
            &nbsp;{dayName(task.day)} {task.day} {task.time && "@"} {task.time}
          </p>
        </div>
      </div>
    </div>
  );
};
export default TaskEdit;
