import TaskSwitch from "./TaskSwitch";
import { DragDropContext, Droppable,dragg } from 'react-beautiful-dnd'
const Tasks = ({ tasks, deleteTask, toggleReminder, editTask }) => {
  return (
    <DragDropContext>
      <Droppable>
        {tasks.map((task, i) => (
          <TaskSwitch
            key={task.id}
            task={task}
            deleteTask={deleteTask}
            toggleReminder={toggleReminder}
            editTask={editTask}
          />
        ))}
      </Droppable>
    </DragDropContext>
  );
};

export default Tasks;
