import { useState } from "react";
import { FaTimes, FaClock, FaEdit, FaEquals } from "react-icons/fa";
import { dayName } from "../utls/date";
const Task = ({ task, setMnueItem }) => {
  return (
    <div>
    <FaEquals/>
      <div>

        <h3
          onClick={() => {
            setMnueItem(2);
          }}
        >
          {task.text}
        </h3>

        <p className="time">
          <FaClock fontSize="10" />
          &nbsp;{dayName(task.day)} {task.day} {task.time && "@"} {task.time}
        </p>
      </div>
    </div>
  );
};
export default Task;
