import { useState, useEffect } from "react";
import { BrowserRouter as Router, Route } from "react-router-dom";

import Header from "./componenets/Header";
import Tasks from "./componenets/Tasks";
import AddTask from "./componenets/AddTask";
import Footer from "./componenets/Footer";
import About from "./componenets/About";

function App(props) {
  const [tasks, setTasks] = useState([]);
  const [showAddTaskForm, setShowAddTaskForm] = useState(false);

  useEffect(() => {
    const getTasks = async () => {
      const tasksFromServer = await fetchTasks();
      setTasks(tasksFromServer);
    };
    getTasks();
  }, []);

  //REST
  // fetch Tasks
  const fetchTasks = async () => {
    const res = await fetch("http://localhost:8000/tasks");
    const data = await res.json();

    return data;
  };

  // fetch Task
  const fetchTask = async (id) => {
    const res = await fetch(`http://localhost:8000/tasks/${id}`, {
      method: "GET",
    });
    const data = await res.json();
    return data;
  };

  // Update data
  const updateData = async (id, data) => {
    await fetch(`http://localhost:8000/tasks/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    });
  };

  // Delete Task
  const deleteTask = async (id) => {
    await fetch(`http://localhost:8000/tasks/${id}`, { method: "DELETE" });
    setTasks(tasks.filter((task) => task.id !== id));
  };

  // Post

  // Toggle reminder
  const toggleReminder = async (id) => {
    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, reminder: !task.reminder } : task
      )
    );

    const taskToChange = await fetchTask(id);

    const changedTask = await {
      ...taskToChange,
      reminder: !taskToChange.reminder,
    };

    updateData(id, changedTask);
  };

  // Add Task
  const addTask = async (task) => {
    const res = await fetch("http://localhost:8000/tasks", {
      method: "POST",
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify(task),
    });
    const data = await res.json();
    setTasks([...tasks, data]);

    // const newTask = { id: tasks.length + 1, ...task };
    // setTasks([...tasks, newTask]);
  };

  //Edit Task text
  const editTask =async (payload) => {
    // Fetch tasks
    const tasks = await fetchTasks()
    // Fetch oldTaskInfo
     //const oldTaskInfo = await fetchTask(payload.id)//
    //  console.log('-->',payload,oldTaskInfo);
    // Replace oldTaskInfo with newTaskInfo

     const newTasks= tasks.map(task =>  payload.id === task.id ? {...task, text: payload.text}:task)
     setTasks(newTasks)
    console.log(newTasks);

    // Update Tasks state with newTaskInfo
    // PUT newTaskInfo to server
    updateData(payload.id,payload)
  }

  return (
    <Router>
      <div className="container">
        <Header
          showAddTaskForm={showAddTaskForm}
          toggleAddTaskForm={() => setShowAddTaskForm(!showAddTaskForm)}
        />

        <Route
          path="/"
          exact
          render={(props) => (
            <>
              {showAddTaskForm && (
                <AddTask addTask={addTask} showAddTaskForm={showAddTaskForm} />
              )}

              {tasks.length > 0 ? (
                <Tasks
                  tasks={tasks}
                  setTasks={setTasks}
                  deleteTask={deleteTask}
                  toggleReminder={toggleReminder}
                  editTask={editTask}
                />
              ) : (
                <div className="task" style={{ backgroundColor: "white" }}>
                  Sorry.. No tasks to show!
                </div>
              )}
            </>
          )}
        />
        <Route path="/about" component={About} />
        <Footer />
      </div>
    </Router>
  );
}

export default App;
