import Task from "./Task";
const tasks = [
  {
    id: 1,
    text: "Doctors Appointment",
    day: "Feb 5th at 2:30pm",
    reminder: true,
  },
  {
    id: 2,
    text: "Meeting at School",
    day: "Feb 6th at 1:30pm",
    reminder: true,
  },
];
const Tasks = () => {
  return (
    <>
      {tasks.map((task, i) => {
        return (
          <Task 
            id={task.id}
            text={task.text}
            day={task.day}
            reminder={task.reminder}
          />
        );
      })}
    </>
  );
};

export default Tasks;
