import { useState } from "react";
import { FaTimes, FaClock, FaEdit,FaCheck } from "react-icons/fa";
import { dayName } from "../utls/date";

const Task = ({ task, deleteTask, toggleReminder }) => {
  const [editTaskToggle, setEditTaskToggle] = useState(false);
  const [editTask,setEditTask] = useState(task.text);
console.log(editTask.length,Math.ceil(editTask.length/32));
  return (
    <div
      className={`task ${task.reminder ? "reminder" : ""}`}
      onDoubleClick={() => toggleReminder(task.id)}
    >
      <div>
        {editTaskToggle ? (
          <form>
            <textarea
              className="edit"
              type="text-area"
              value={editTask}
              rows={editTask.length < 32 ? 1 : Math.ceil(editTask.length / 32)}
              cols={editTask.length < 40 ? editTask.length : 40}
              onChange={(e) => setEditTask(e.target.value)}
            />


          </form>
        ) : (
          <h3 onClick={() => setEditTaskToggle(!editTaskToggle)}>
            {task.text}
          </h3>
        )}
        <p>
          <FaClock color="gray" fontSize="10" />
          &nbsp;{dayName(task.day)} {task.day} {task.time && "@"} {task.time}
        </p>
      </div>
      {!editTaskToggle&&(<div className="" style={{ width: "100" }}>
        <FaEdit
          style={{
            cursor: "pointer",
            fontSize: "12px",
            marginRight: "10px",
            paddingBottom: "2px",
          }}
          onClick={() => setEditTaskToggle(!editTaskToggle)}
        />
        <FaTimes
          style={{ color: "red", cursor: "pointer" }}
          onClick={() => deleteTask(task.id)}
        />
      </div>)}
    </div>
  );
};
export default Task;
