  return (
    <header className="header">
      <h1>{title}</h1>

      {location === "/" && (
        <Button
          color={showAddTaskForm ? "red" : "black"}
          text={showAddTaskForm ? "close" : "+ Add Task"}
          count={1}
          onClick={toggleAddTaskForm}
          on
